DROP TABLE IF EXISTS `unit_rule`;
CREATE TABLE `unit_rule` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `created_stime` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `modified_stime` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
  `is_del` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否删除',
  `rule_type` varchar(30) NOT NULL COMMENT '规则类型:mood语气词,ok肯定意图,reject 拒绝意图,other 其他意图',
  `like_mode` varchar(30) NOT NULL COMMENT '匹配规则:all,perfix, suffix, similiary',
  `sentence` varchar(255) NOT NULL COMMENT '例句',
  `pattern` varchar(255) DEFAULT NULL COMMENT '正则表达式',
  `similar_rate` decimal(10,2) DEFAULT NULL COMMENT '相似度',
  `action` varchar(30) NOT NULL DEFAULT '0' COMMENT '动作:ignore、 interrupt 、 repeat 、hangup、next',
  `remark` varchar(255) DEFAULT NULL COMMENT '备注',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=218 DEFAULT CHARSET=utf8mb4 COMMENT='机器人规则表';

